import React,{useState,useEffect,useContext, useCallback} from 'react'
import {UserContext} from '../../App'
import {useParams} from 'react-router-dom'

const Home=()=>{
    const [data,setData]=useState([])
    const {state,dispatch}=useContext(UserContext)
    // const {userid} = useParams()
    // const [userProfile,setProfile] = useState(null)
    // const id=JSON.parse(state)._id
    // console.log(state)
    // useEffect(()=>{
    //     fetch(`/user/${userid}`,{
    //         headers:{
    //             "Authorization":"Bearer "+localStorage.getItem("jwt")
    //         }
    //     })
    //     .then(res=>res.json())
    //     .then(result=>{
    //         setProfile(result)
    //     })
    //  },[])

    useEffect(()=>{
        fetch('/allpost',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        })
        .then(res=>res.json())
        .then(result=>{
           console.log(result)
           setData(result.posts)
       })  
    },[])

       

    const likePost = (id)=>{
        fetch('/like',{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                postId:id
            })
        }).then(res=>res.json())
        .then(result=>{
            //console.log(result)
            const newData = data.map(item=>{
                if(item._id==result._id){
                    console.log(item._id)
                    return result
                }else{
                    console.log(item._id)
                    return item
                }
            })
        setData(newData)
        }).catch(err=>{
            console.log(err)
        })
    }
    const unlikePost = (id)=>{
        fetch('/unlike',{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                postId:id
            })
        }).then(res=>res.json())
        .then(result=>{
            // console.log(result)
            const newData = data.map(item=>{
                if(item._id==result._id){
                    return result
                }else{
                    return item
                    
                }
            })
        setData(newData)
        
        }).catch(err=>{
            console.log(err)
        })
    }

    

    return(
        <div className="home">
            {
                data.map(item=>{
                    return(
                        <div className="card home-card" key={item._id}>
                            <h5>{item.postedBy.name}</h5>
                            <div className="card-content">
                            {item.likes.includes(state._id) 
                                ? 
                                (<i
                                className="material-icons"
                                onClick={() => {
                                    unlikePost(item._id);
                                }}
                                style={{ color: "red" }}
                                >
                                favorite
                                </i>
                                )
                                : 
                                (<i
                                className="material-icons"
                                onClick={() => {
                                    likePost(item._id);
                                }}
                                style={{ color: "red" }}
                                >
                                favorite_border
                                </i>
                            )}
                                {/* {button(item.likes)} */}
                                <h6>{item.likes.length} likes</h6>
                                <h6>{item.title}</h6>
                                <p>{item.body}</p>
                                <input type="text" placeholder="add a comment"></input>
                            </div>
                        </div>
                    )
                })
            }
            
        </div>
    )
}

export default Home
