import React,{useState,useContext} from 'react'
import {useHistory,Switch} from 'react-router-dom'
import M from 'materialize-css'

const CreatePost =()=>{
    const history=useHistory()
    const[title,setTitle]=useState("")
    const[body,setBody]=useState("")
    const[image,setImage]=useState("")
    const[url,setUrl]=useState("")

    const postDetails=()=>{
        const data=new FormData()
        data.append("file",image)
        data.append("upload_preset","alumni-tracker")
        data.append("cloud_name","aditicloudinary1")
        fetch("https://api.cloudinary.com/v1_1/aditicloudinary1/image/upload",{
            method:"post",
            body:data
        })
        .then(res=>res.json())
        .then(data=>{
            setUrl(data.url)
        })
        .catch(err=>{
            console.log(err)
        })
        fetch("/create",{
            method:"post",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                title,
                body,
                pic:url
            })
        }).then(res=>res.json())
        .then(data=>{
            console.log(data)
           if(data.error){
              M.toast({html: data.error,classes:"#c62828 red darken-3"})
           }
           else{
               M.toast({html:"Posted",classes:"#43a047 green darken-1"})
               history.push('/')
           }
        }).catch(err=>{
            console.log(err)
        })
    }

    return(
        <div className="card input-field">
            <input type="text" 
            placeholder="title"
            value={title}
            onChange={(e)=>setTitle(e.target.value)}
            />
            <input type="text" 
            placeholder="body"
            value={body}
            onChange={(e)=>setBody(e.target.value)}
            />
            <div className="file-field input-field">
                <div className="btn black darken">
                    <span>Upload</span>
                    <input type="file" 
                    onChange={(e)=>setImage(e.target.files[0])}
                    />
                </div>
                <div className="file-path-wrapper">
                    <input className="file-path validate" type="text"/>
                </div>
                </div>
                <button className="btn waves-effect waves-light black darken-1"
                onClick={()=>postDetails()}
                >
                    POST
                </button>
        </div>
    )
}

export default CreatePost
