import React,{useState} from 'react'


const CreatePost =()=>{
    const [title,setTitle]=useState("")
    const[body,setBody]=useState("")
    const[image,setImage]=useState("")
    const postDetails=()=>{
        const data=new FormData()
        data.append("file",image)
        data.append("upload_preset","alumni-tracker")
        data.append("cloud_name","aditicloudinary1")
        fetch("https://api.cloudinary.com/v1_1/aditicloudinary1/image/upload")
    }

    return(
        <div className="card input-field"        >
            <input type="text" 
            placeholder="title"
            onChange={(e)=>setTitle(e.target.value)}
            />
            <input type="text" 
            placeholder="body"
            onChange={(e)=>setBody(e.target.value)}
            />
            <div className="file-field input-field">
                <div className="btn black darken">
                    <span>Upload</span>
                    <input type="file" onChange={(e)=>console.log(e.target.files)}/>
                </div>
                <div className="file-path-wrapper">
                    <input className="file-path validate" type="text" onChange={(e)=>setImage(e.target.files[0])}/>
                </div>
                </div>
                <button className="btn waves-effect waves-light black darken-1">
                    POST
                </button>
        </div>
    )
}

export default CreatePost
